import classes from "./FoodItemForm.module.css";
import Input from "../../UI/Input";

const FoodItemForm = (props) => {
  return (
    <>
      <form className={classes.form}>
        <Input 
          label="Amount" 
          input={{
          id:"amount" + props.id, 
          type:"number", 
          min:"1", 
          max:"5", 
          step:"1", 
          defaultValue:"1"
        }}
        />
        <button>Add to Cart</button>
      </form>
    </>
  ) 
};

export default FoodItemForm;
